    function hex_bytes(n) {
        var num = 0;
        for (; n > 0; --n) {
            var digit = parseInt(next(true), 16);
            if (isNaN(digit))
                parse_error("SyntaxError: Invalid hex-character pattern in string");
            num = (num << 4) | digit;
